Sql.query:
SELECT EMPRCODI,(SELECT EMPRNOMB FROM VW_SI_EMPRESA WHERE VW_SI_EMPRESA.EMPRCODI=trn_valor_trans.EMPRCODI) AS EMPRNOMB,
(select VTRANETOTAL FROM trn_valor_trans_empresa where trn_valor_trans_empresa.EMPRCODI= trn_valor_trans.EMPRCODI and trn_valor_trans_empresa.PERICODI = trn_valor_trans.PERICODI AND trn_valor_trans_empresa.VTRANEVERSION = trn_valor_trans.VTRANVERSION
) AS VT, (SELECT SALEMPSALDO FROM TRN_SALDO_EMPRESA WHERE TRN_SALDO_EMPRESA.EMPRCODI =trn_valor_trans.EMPRCODI AND TRN_SALDO_EMPRESA.PERICODI =trn_valor_trans.PERICODI AND TRN_SALDO_EMPRESA.SALEMPVERSION=trn_valor_trans.VTRANVERSION )AS ST, (SELECT SUM(INGCOMIMPORTE) FROM TRN_ING_COMPENSACION WHERE TRN_ING_COMPENSACION.EMPRCODI=trn_valor_trans.EMPRCODI AND TRN_ING_COMPENSACION.PERICODI =trn_valor_trans.PERICODI AND TRN_ING_COMPENSACION.INGCOMVERSION= trn_valor_trans.VTRANVERSION
) AS COMPENSACION ,(select vtotemtotal from trn_valor_total_empresa where trn_valor_total_empresa.EMPRCODI =trn_valor_trans.EMPRCODI AND trn_valor_total_empresa.VTOTEMVERSION = trn_valor_trans.VTRANVERSION AND trn_valor_total_empresa.PERICODI = trn_valor_trans.PERICODI
)AS TOTAL ,PERICODI,VTRANVERSION from trn_valor_trans where (PERICODI=:PERICODI )AND (VTRANVERSION =:VTRANVERSION) group by EMPRCODI,PERICODI,VTRANVERSION ORDER BY EMPRCODI ASC
